import React from 'react';
import { Link } from 'react-router-dom';

class Debits extends React.Component {
  constructor(props) {
    super(props);

    // Initialize the state for this component
    this.state = {
      description: '',
      amount: '',
    };
  }

  handleInputChange = (event) => {
    // Update the state when the input fields change
    const { name, value } = event.target;
    this.setState({ [name]: value });
  };

  handleSubmit = (event) => {
    event.preventDefault();

    // Create a new debit object using the form data
    const newDebit = {
      description: this.state.description,
      amount: parseFloat(this.state.amount),
      // Add a date property if needed
    };

    // Call a function to add the new debit to your data (not shown in this snippet)
    this.props.addDebit(newDebit);

    // Clear the form after submitting
    this.setState({
      description: '',
      amount: '',
    });
  };

  render() {
    const { debits } = this.props;

    const debitItems = debits.map((debit) => (
      <li key={debit.id}>
        {debit.amount} {debit.description} {debit.date}
      </li>
    ));

    return (
      <div>
        <h1>Debits</h1>

        {debitItems}

        <form onSubmit={this.handleSubmit}>
          <input
            type="text"
            name="description"
            value={this.state.description}
            onChange={this.handleInputChange}
            placeholder="Description"
          />
          <input
            type="number"
            name="amount"
            value={this.state.amount}
            onChange={this.handleInputChange}
            placeholder="Amount"
          />
          <button type="submit">Add Debit</button>
        </form>
        <br />
        <Link to="/">Return to Home</Link>
      </div>
    );
  }
}

export default Debits;
